import { Children } from "react";
import { loader, emptyList } from "./helpers";

const Each = ({ emptyMessage = "Empty List", isLoading = false, of, render, table = false }) => {
  if (isLoading) return loader();
  if (!of || of.length === 0) return emptyList(emptyMessage, table);
  return <>{Children.toArray(of.map((item, index) => render(item, index)))}</>;
};

export default Each;
